import axios from 'axios';

const BASE_URL = '8.242.76.158';
const PORT = 443;
export const API_URL = `${BASE_URL}:${PORT}`;

// URL base do frontend (mesma que a API neste caso)
export const FRONTEND_URL = API_URL;

export const api = axios.create({
  baseURL: `${API_URL}/api`,
  timeout: 30000, // 30 segundos
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

// Interceptor para adicionar o token de autenticação
api.interceptors.request.use((config) => {
  try {
    const token = localStorage.getItem('token');
    if (token && config.headers) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  } catch (error) {
    console.error('Erro ao processar token:', error);
    return config;
  }
}, (error) => {
  console.error('Erro no interceptor:', error);
  return Promise.reject(error);
});

// Interceptor para tratar erros de resposta
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token inválido ou expirado
      localStorage.removeItem('token');
      window.location.href = '/';
    }
    return Promise.reject(error);
  }
);

export interface TermoData {
  nome: string;
  sobrenome: string;
  email: string;
  equipamento: string;
  equipe: string;
  data: string;
  urlAcesso?: string;
  assinatura?: string;
}

export interface TermoResponse {
  id: string;
  urlAcesso: string;
}

export interface TermoDetalhes {
  id: string;
  nome: string;
  sobrenome: string;
  email: string;
  equipamento: string;
  status: 'pendente' | 'assinado';
  dataCriacao: string;
  urlAcesso: string;
  assinatura?: string;
}

export interface CriarTermoDTO {
  nome: string;
  sobrenome: string;
  email: string;
  equipamento: string;
}

export interface AtualizarStatusDTO {
  status: 'assinado';
  assinatura: string;
}

export const TermoService = {
  // Criar novo termo
  criar: async (data: CriarTermoDTO): Promise<TermoDetalhes> => {
    try {
      console.log('Enviando dados para criar termo:', data);
      const response = await api.post<TermoDetalhes>('/termos', data);
      console.log('Resposta do servidor:', response.data);
      
      if (!response.data || !response.data.id) {
        throw new Error('Resposta do servidor não contém os dados esperados');
      }
      
      return response.data;
    } catch (error: any) {
      console.error('Erro detalhado ao criar termo:', error.response?.data || error.message);
      throw new Error(error.response?.data?.message || 'Erro ao criar termo. Por favor, tente novamente.');
    }
  },

  // Buscar termo por ID
  buscarPorId: async (id: string): Promise<TermoDetalhes> => {
    const response = await api.get<TermoDetalhes>(`/termos/${id}`);
    return response.data;
  },

  // Listar todos os termos
  listar: async (): Promise<TermoDetalhes[]> => {
    const response = await api.get<TermoDetalhes[]>('/termos');
    return response.data;
  },

  // Atualizar status do termo
  atualizarStatus: async (id: string, data: AtualizarStatusDTO): Promise<TermoDetalhes> => {
    const response = await api.patch<TermoDetalhes>(`/termos/${id}/status`, data);
    return response.data;
  },

  // Excluir termo
  excluir: async (id: string): Promise<void> => {
    await api.delete(`/termos/${id}`);
  },

  // Gerar URL de acesso para assinatura
  gerarUrlAcesso(id: string): string {
    return `${API_URL}/assinar/${id}`;
  },

  downloadPDF: async (id: string): Promise<Blob> => {
    try {
      const response = await api.get(`/termos/${id}/pdf`, {
        responseType: 'arraybuffer'
      });

      return new Blob([response.data as ArrayBuffer], { type: 'application/pdf' });
    } catch (error) {
      console.error('Erro ao baixar PDF:', error);
      throw error;
    }
  },

  gerarLinkAssinatura: (id: string): string => {
    return `${API_URL}/assinar/${id}`;
  },

  gerarLinkVisualizacao: (id: string): string => {
    return `${API_URL}/visualizar/${id}`;
  }
};

export default api;
